program.vertex:
void main(void)
{
  gl_Position = uMvpMatrix * vec4(aPosition*uSize.xy, 0.0, 1.0);
  vTexCoord = mix( uTextureRect.xy, uTextureRect.zw, aPosition + vec2(0.5) );
}

program.fragment:
precision mediump float;
uniform float uAmplitude; // 0.02; (< 1)
uniform float uTime;
void main()
{
  highp vec2 textureSize = uTextureRect.zw - uTextureRect.xy;
  highp vec2 pos = -1.0 + 2.0 * vTexCoord.st/textureSize;
  highp float len = length(pos);
  highp vec2 texCoord = vTexCoord.st/textureSize + pos/len * sin( len * 12.0 - uTime * 4.0 ) * uAmplitude; 
  gl_FragColor = texture2D(sTexture, texCoord) * uColor;
}
